const active=document.querySelector(".menu__btn");active.addEventListener("click",()=>{const e=document.querySelector(".menu__list"),t=document.querySelector(".header");e.classList.toggle("menu__list--active"),e.classList.contains("menu__list--active")?t.style.backgroundColor="#3f3fde":t.style.backgroundColor="#3e94ff"});const smoothLinks=document.querySelectorAll('a[href^="#"]');for(let t of smoothLinks)t.addEventListener("click",function(e){e.preventDefault();e=t.getAttribute("href");document.querySelector(e).scrollIntoView({behavior:"smooth",block:"start"})});